import { Scroller } from "@/registry/default/ui/scroller";

export default function ScrollerNavigationDemo() {
  return (
    <Scroller
      withNavigation
      scrollTriggerMode="press"
      className="h-80 w-full p-4"
    >
      {Array.from({ length: 10 }).map((_, index) => (
        <div
          key={index}
          className="mb-4 flex flex-col rounded-md bg-accent p-4"
        >
          <div className="font-medium text-lg">Section {index + 1}</div>
          <span className="text-muted-foreground text-sm">
            Use the navigation arrows to scroll
          </span>
          <p className="mt-2 text-muted-foreground text-sm">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua.
          </p>
        </div>
      ))}
    </Scroller>
  );
}
